mapper.select:
select  b.BNO,b.TITLE,b.MLOCA,b.MTIME,b.MDAY,b.DISTANCE,b.TIME,b.PNUM,
        b.PH,b.CDT,b.RBTYPE,b.MNO,b.LOCA,m.MNM 
from RIDINGBOARD b inner join MEMBERS m
where RBTYPE=#{rbtype} and loca=#{loca}
order by BNO DESC
limit #{startIndex}, #{length}

select  BNO,TITLE,MLOCA,MTIME,MDAY,DISTANCE,TIME,PNUM,PH,CDT,RBTYPE,MNO,LOCA 
from RIDINGBOARD 
where MNO=#{no} and MDAY>=now() and RBTYPE=#{rbtype}
order by BNO DESC
limit #{startIndex}, #{length}

select  BNO,TITLE,MLOCA,MTIME,MDAY,DISTANCE,TIME,PNUM,PH,CDT,RBTYPE,MNO,LOCA 
from RIDINGBOARD 
where MNO=#{no} and MDAY  <  now() and RBTYPE=#{rbtype}
order by BNO DESC
limit #{startIndex}, #{length}

select IMGLO 
from RBIMG  
where BNO=#{bno}

select count(*)
from MEMBERLIST
where BNO=#{bno} and MNO=#{mno}
